package com.example.examscanner.image_processing;public interface ScanAnswersConsumer {    /**     * @param numOfAnswersDetected How much answers have been detected     * @param answersIds           answersIds[i] = the answer number of the i'th detection     * @param lefts                lefts[i] = the leftmost x value of the i'th detected answer     * @param tops                 tops[i] = the top y value of the i'th detected answer     * @param rights               rights[i] = the rightmost x value of the i'th detected answer     * @param bottoms              bottoms[i] = the bottom y value of the i'th detected answer     * @param selections           selections[i] = the marked answer of the i'th answer     */    public void consume(int numOfAnswersDetected,int[] answersIds,int[] lefts,int[] tops,int[] rights,int[] bottoms,int[] selections);}